const ID=[{modal:"Modal_card-location",btn:"btnModal_card-location",close:"close-c-l"},{modal:"Modal_card-location2",btn:"btnModal_card-location",close:"close-c-l"}];const id_modal=[];const id_btn=[];const id_close=[];for(const contando in ID){id_modal.push(ID[contando].modal);id_btn.push(ID[contando].btn);id_close.push(ID[contando].close)}
function modal(id){var ids=id;let modal=ids.substr(3,ids.lenght);document.getElementById(modal).style.display="block";localStorage.setItem("modal",modal);console.log(modal)}
function modal_close(id){document.getElementsByClassName(id)[0];document.getElementById(localStorage.getItem("modal")).style.display="none"}
window.onclick=function(event){if(event.target==document.getElementById(localStorage.getItem("modal"))){document.getElementById(localStorage.getItem("modal")).style.display="none"}}